INSERT INTO `constru_structure` VALUES (10, '数字化技术应用状况', 2, 2, '年报中的词汇能够侧面反映出企业的发展规划，很大程度能够体现企业经营理念指导下的转型方式，反映企业对于数字应用的重视程度。');
