INSERT INTO `constru_structure` VALUES (26, '员工总数', 6, 3, '从员工数量方面反映企业规模状况，员工数量越多反映企业资金实力越雄厚，能够反映企业在人力资源方面的配备以及是否为数字化转型做好人员储备。', '人');
INSERT INTO `constru_structure` VALUES (27, '资产负债率', 7, 3, '反映企业经营风险的大小，也反映企业利用债权人提供的资金从事经营活动的能力，可以衡量在数字化转型中企业调动资金的能力。', '人');
INSERT INTO `constru_structure` VALUES (28, '总资产周转率', 7, 3, '企业一定时期的销售收入净额与平均资产总额之比,它是衡量资产投资规模与销售水平之间配比情况的指标，可以衡量企业数字化资产投资规模。', '%');
INSERT INTO `constru_structure` VALUES (29, '净利润增长率', 7, 3, '反映企业持续盈利能力，企业持续盈利是实现数字化转型和投入的基础条件，成长较快的企业越有趋势运用数字化技术加速发展。', '%');
INSERT INTO `constru_structure` VALUES (30, '营业净利润率', 7, 3, '反映企业经营效率，经营效率越高，企业数字化转型的支撑力就更强。', '%');
INSERT INTO `constru_structure` VALUES (31, '研发投入费用', 8, 3, '直接反映了公司在研发上财务资源投入，研发投入可以直接助推企业运用数字化技术，促进数字化转型进程。', '%');
INSERT INTO `constru_structure` VALUES (32, '研发投入占营业收入比例', 8, 3, '指标用于刻画企业技术创新潜能，反映企业在研发上的投入强度，从而在数字化技术层面推动数字化转型。', '元');
INSERT INTO `constru_structure` VALUES (33, '研发人员', 8, 3, '背后反映了公司隐性知识的数量,研发人员占比越高代表企业对于研发重视程度越高，从而有利于企业在技术层面进行数字化转型。', '%');
INSERT INTO `constru_structure` VALUES (34, '研发项目个数', 8, 3, '反映企业研发所涉及的广度，项目数量越多代表企业投入越多，对于转型越重视。', '人');
INSERT INTO `constru_structure` VALUES (35, '专利数', 8, 3, '专利可以被理解为企业所拥有的显性知识，专利数量越多，企业科技创新能力越强，从而推动企业转型。', '个');
INSERT INTO `constru_structure` VALUES (36, '硕士及以上人员数量', 8, 3, '反映建筑公司人员学历结构，硕士学历及以上的人员数量越多，代表企业员工知识层次越高，越有可能推动企业创新，促进数字化转型。', '个');
INSERT INTO `constru_structure` VALUES (37, '国家级获奖', 8, 3, '反映企业高水平创新产出数量，数量越多代表企业在科技创新领域实力越强，具有领袖作用，越有可能推动数字化转型。', '人');
INSERT INTO `constru_structure` VALUES (38, '研发中心数量', 8, 3, '反映企业开设的研发机构数量，形成集聚性的研发创新力，从而推动数字化转型。', '个');
INSERT INTO `constru_structure` VALUES (39, '数字化创新平台数', 9, 3, '反映企业依托科研单位进行产学研的程度，程度越高代表企业与高校联系越来密切，越可能依托高校科研实力完成数字化转型。', '个');
INSERT INTO `constru_structure` VALUES (40, '互联网云平台运用数量', 9, 3, '反映企业数字化互联网平台运用状况，互联网平台也是企业运用数字化技术的表现之一，因此通过运用互联网平台反映企业数字化程度。', '个');
INSERT INTO `constru_structure` VALUES (41, '数字化业务板块数', 9, 3, '反映企业其所含业务板块中数字化应用程度，业务板块数量越多，说明企业涵盖数字化业务范围越广。', '个');
INSERT INTO `constru_structure` VALUES (42, '联合高等院校课题研发数', 9, 3, '反映企业与科研单位合作研发的状况，程度越高代表企业与研发单位联系越来密切，越可能率先完成数字化转型。', '个');
INSERT INTO `constru_structure` VALUES (43, '成果转化数', 9, 3, '反映企业将数字化技术转化成成果的程度，程度越高说明数字化技术应用越广泛。', '个');
INSERT INTO `constru_structure` VALUES (44, '一级技术词频数', 10, 3, '年报中的词汇能够侧面反映出企业的发展规划，很大程度能够体现企业经营理念指导下的转型方式，反映企业对于数字应用的重视程度。', '个');
INSERT INTO `constru_structure` VALUES (45, '二级技术词频数', 10, 3, '年报中的词汇能够侧面反映出企业的发展规划，很大程度能够体现企业经营理念指导下的转型方式，反映企业对于数字应用的重视程度。', '次');
INSERT INTO `constru_structure` VALUES (46, '三级技术词频数', 10, 3, '年报中的词汇能够侧面反映出企业的发展规划，很大程度能够体现企业经营理念指导下的转型方式，反映企业对于数字应用的重视程度。', '次');
INSERT INTO `constru_structure` VALUES (47, '业务板块数量（多元化情况）', 11, 3, '通过企业业务板块数量来反映企业发展的多元化情况，企业业务多元化程度越高，企业探索新领域、推进数字化转型的意愿越强。', '次');
INSERT INTO `constru_structure` VALUES (48, '组织结构中是否有专门的数字化部门', 11, 3, NULL, NULL);
INSERT INTO `constru_structure` VALUES (49, '组织内部是否具有专门的项目管理系统', 11, 3, '组织内部的项目管理系统是集成了项目信息、业务数据的数字化系统或平台，能够从组织结构层面体现企业的数字化程度。', '个');
INSERT INTO `constru_structure` VALUES (50, '是否与数字化类型的企业有合作、收购、投资关系（从年报中获取）', 12, 3, '反映企业对数字化相关业务、企业的重视，是开启数字化转型的有益尝试。', '/');
INSERT INTO `constru_structure` VALUES (51, '是否为国家认定高新技术企业', 12, 3, '反映企业的研发创新能力被国家认可，并受到国家和政府的政策支持，为进一步研发创新，实现数字化转型提供强大推力。', '/');
INSERT INTO `constru_structure` VALUES (52, '企业年报中是否有提及数字化战略的相关内容？', 13, 3, '反映了企业未来经营计划中对数字化的思考，为未来实现数字化提供战略指引。', '/');
INSERT INTO `constru_structure` VALUES (53, '企业年报中是否有专项数字化转型报告', 13, 3, '年报中有单独的小节、段落来介绍企业的数字化情况能够反映企业数字化成果丰富，且重视企业数字化的发展。', '/');
INSERT INTO `constru_structure` VALUES (54, '企业远景、目标、定位中是否具有数字化、大数据、智能化、信息化等词语', 13, 3, '反映企业对数字化相关战略的重视程度，数字化战略在企业远景规划中扮演的角色与地位。', '/');
INSERT INTO `constru_structure` VALUES (55, '企业战略职能中是否具有专门数字化业务部门', 14, 3, '专门的数字化业务部门能够统筹安排企业的数字化相关工作，为企业的数字化转型提供业务支持。', '/');
INSERT INTO `constru_structure` VALUES (56, '企业业务中是否运用信息化技术系统', 14, 3, '应用信息化技术系统越多，反映企业的信息化程度越高，为数字化转型提供良好的技术基础。', '/');
INSERT INTO `constru_structure` VALUES (57, ' 企业业务中是否运用信息化技术系统个数', 14, 3, '应用信息化技术系统越多，反映企业的信息化程度越高，为数字化转型提供良好的技术基础。', '/');
INSERT INTO `constru_structure` VALUES (58, '企业年报中是否有提及“政策”或相关对于数字化转型政策支持的词语', 15, 3, '政府的相关政策为企业进行数字化转型提供保障和推动力。', '/');
INSERT INTO `constru_structure` VALUES (59, '企业年报中是否有提及“行业”、“市场”、“需求”或相关对于数字化转型外部驱动的词语', 15, 3, '行业、市场等外部需求能够推动企业加速进行数字化转型。', '次');
INSERT INTO `constru_structure` VALUES (60, '装配式建筑项目数', 16, 3, '反映施工企业装配式建筑的应用情况，装配式建筑由于标准化设计、工厂化生产、信息化管理等特点，因此是施工企业数字化程度的直接体现。', '次');
INSERT INTO `constru_structure` VALUES (61, '完成装配式建筑面积', 16, 3, '反映施工企业装配式建筑的应用情况，装配式建筑由于标准化设计、工厂化生产、信息化管理等特点，因此是施工企业数字化程度的直接体现。', '个');
INSERT INTO `constru_structure` VALUES (62, '装配式建筑产业基地数量', 16, 3, '反映施工企业装配式建筑的应用情况，装配式建筑由于标准化设计、工厂化生产、信息化管理等特点，因此是施工企业数字化程度的直接体现。', '平方米');
INSERT INTO `constru_structure` VALUES (63, '拥有建筑机器人数量', 17, 3, '企业将数字化技术应用在施工场景中，施工单位高质量智慧工地数量越多说明企业在施工安全、效率等问题上重视程度越高，越有趋势运用数字化技术保障施工安全和效率。', '个');
INSERT INTO `constru_structure` VALUES (64, '智慧工地数量占比', 17, 3, '企业将数字化技术应用在施工场景中，施工单位高质量智慧工地数量越多说明企业在施工安全、效率等问题上重视程度越高，越有趋势运用数字化技术保障施工安全和效率。', '个');
INSERT INTO `constru_structure` VALUES (65, '5G技术应用工地数量', 17, 3, '企业将数字化技术应用在施工场景中，施工单位高质量智慧工地数量越多说明企业在施工安全、效率等问题上重视程度越高，越有趋势运用数字化技术保障施工安全和效率。', '%');
INSERT INTO `constru_structure` VALUES (66, '运用BIM技术合同额', 18, 3, '反映企业运用BIM技术的状况以及BIM技术运用水平，BIM技术作为建筑领域数字化转型代表技术，运用程度可以反映数字化转型程度。', '个');
INSERT INTO `constru_structure` VALUES (67, '采用BIM技术项目个数', 18, 3, '反映企业运用BIM技术的状况以及BIM技术运用水平，BIM技术作为建筑领域数字化转型代表技术，运用程度可以反映数字化转型程度。', '元');
INSERT INTO `constru_structure` VALUES (68, '智能建造平台数量', 19, 3, '反映施工管理过程中的电子化、信息化程度，电子化的工程档案、签章等能够大大提高施工管理的效率，是施工企业数字化程度的重要体现。', '个');
INSERT INTO `constru_structure` VALUES (69, '采用电子签名/签章项目占比', 19, 3, '反映施工管理过程中的电子化、信息化程度，电子化的工程档案、签章等能够大大提高施工管理的效率，是施工企业数字化程度的重要体现。', '个');
INSERT INTO `constru_structure` VALUES (70, '拥有电子化工程档案项目占比', 19, 3, '反映施工管理过程中的电子化、信息化程度，电子化的工程档案、签章等能够大大提高施工管理的效率，是施工企业数字化程度的重要体现。', '%');
INSERT INTO `constru_structure` VALUES (71, 'A、智能塔吊', 20, 3, '反映智能设备在施工过程中的应用情况，智能施工设施的应用是建筑业转型的重要体现，也是研发创新的成果体现，应用的智能设施越多，反映企业数字化程度越高。', '%');
INSERT INTO `design_enterprise` VALUES ('000779', '甘咨询', '工程咨询服务。', '从事规划咨询、咨询评估、工程设计、检测认证、项目管理、管理咨询等咨询服务业务（以工商部门核准经营范围为准）。', '公司系由原兰州三毛纺织(集团)有限责任公司独家发起，1997年1月29日筹建，4月改组，以其净资产折国有法人股5582万股，经5月12日发行后，上市时总股本达10082万股，其内部职工股450万股将于1997年5月26日在深交所交易期满半年后上市。', '甘肃省兰州市城关区酒泉路16号新业大厦713室', '西北', '甘肃', '兰州市', '1995-08-01', '38019.237', '正常上市', '1997-05-28', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('002883', '中设股份', '中国城市建设和交通建设领域的规划咨询及勘察设计、工程监理、项目管理等。', '建设工程咨询服务；规划咨询；项目建议书、可研报告的编制；评估咨询；工程项目管理；工程监理；工程招标代理；工程造价咨询；工程勘察（岩土工程、工程测量、水文地质）、测绘；公路工程、市政工程、水运工程（港口、航道）、风景园林、铁道工程、房屋建筑工程、水利工程、农林工程、住宅小区的设计；城市、交通、城市园林绿化、风景名胜区的规划、设计；工程和技术研究、试验、检测；环境监测；企业管理咨询；房地产咨询；软件开发和技术服务；以下经营范围限分公司经营：打字、复印的服务。（依法须经批准的项目，经相关部门批准后方可开展经营活动）', '本公司是由江苏中设工程咨询集团有限公司整体变更设立的一家股份有限公司，以有限公司截至2014年12月31日经审计的净资产79,398,750.60元为基础，按照1：0.503786的折股比例折合实收资本40,000,000元，其余净资产39,398,750.60元计入资本公积。2015年3月19日，公司在江苏省无锡市工商局办理完成登记注册手续，取得注册号为320211000079862的《工商营业执照》，注册资本为4,000万元。', '江苏省无锡市滨湖区锦溪路100号', '华东', '江苏', '无锡市', '1987-08-20', '13025.7852', '正常上市', '2017-06-20', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('002949', '华阳国际', '建筑设计和研发及其延伸业务。', '工程设计及咨询；工程监理；项目管理；工程总承包及所需设备材料的采购和销售；兴办实业（具体项目另行申报）；经营进出口业务。（以上内容法律、行政法规、国务院决定禁止的项目除外，限制的项目取得许可后方可经营）。', '深圳中信设计公司成立于1993年8月9日，由中信深圳公司以货币资金出资设立，公司注册资本50.00万元。法定代表人为汪津。1993年8月9日，深圳中信设计公司取得深圳市工商局核发的《企业法人营业执照》。2001年10月27日，华阳国际前身雅科特股东会议决议一致同意公司名称由原\"深圳市雅科特建筑设计有限公司\"变更为\"深圳市华阳国际建筑设计有限公司\"。2004年11月11日，根据深圳市华阳国际建筑设计有限公司股东会决议一致同意原公司名\"深圳市华阳国际建筑设计有限公司\"更名为\"深圳市华阳国际工程设计有限公司\"。2015年10月11日，唐崇武、徐华芳等全体发起人签署了《发起人协议》，全体发起人同意采取有限责任公司整体变更为股份有限公司的形式，全体股东同意以在审计基准日经审计的账面净资产值230,510,024.74元为依据进行折股，按照1：0.5488比例折合成126,500,000.00股，每股面值一元人民币，剩余部分104,010,024.74元计入资本公积，整体变更设立的股份公司股本为126,500,000.00元。2015年10月22日，深圳市华阳国际工程设计股份有限公司取得了深圳市市场监督管理局换发的91440300192239795N《营业执照》，股份公司正式成立。', '广东省深圳市龙华区民治街道北站社区汇隆商务中心2号楼2618', '华南', '广东', '深圳市', '1993-08-09', '19603.6572', '正常上市', '2019-02-26', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('003013', '地铁设计', '城市轨道交通、市政、建筑等领域的勘察设计、规划咨询、工程总承包等。', '市政工程设计服务；地质勘查技术服务；岩土工程设计服务；其他工程设计服务；房屋建筑工程设计服务；岩土工程勘察服务；岩土工程勘察综合评定服务；工程地球物理勘探服务；工程地质勘察服务；工程水文勘察服务；工程和技术基础科学研究服务；基础地质勘查；工程项目管理服务；工程总承包服务；工程钻探；桩基检测服务；基坑监测服务；地下管线探测；测绘服务；工程技术咨询服务；城乡规划编制；技术进出口；工程勘察设计；编制工程概算、预算服务；消防设施工程设计与施工。（依法须经批准的项目，经相关部门批准后方可开展经营活动）', '发行人系由设计院有限整体变更设立的股份有限公司。2018年7月11日，立信会计师事务所（特殊普通合伙）出具《审计报告》（信会师报字[2018]第ZC10427号），经审计，设计院有限截至2018年5月31日净资产为81,828.77万元。广州地铁设计研究院股份有限公司招股意向书532018年7月26日，广东联信资产评估土地房地产估价有限公司出具《广州地铁设计研究院有限公司拟整体变更设立股份有限公司所涉及其股东全部权益资产评估报告》（联信评报字[2018]第A0592号），经评估，设计院有限截至2018年5月31日的股东全部权益评估价值为221,495.17万元。广州地铁集团于2018年8月9日对设计院有限填报的《国有资产评估项目备案表》予以确认。2018年7月30日，设计院有限召开股东会，同意设计院有限以2018年5月31日为基准日整体变更为股份有限公司，公司名称变更为“广州地铁设计研究院股份有限公司”，以设计院有限截至2018年5月31日经立信会计师事务所（特殊普通合伙）出具的《审计报告》（信会师报字[2018]第ZC10427号）审计的公司账面净资产81,828.77万元，按1：0.439943的比例折合为股份有限公司股份36,000.00万股，每股面值1元，股份有限公司的注册资本为36,000.00万元，未折股净资产余额45,828.77万元计入股份有限公司资本公积。同日，设计院有限全体股东签署了《发起人协议》。2018年8月18日，发行人召开创立大会暨第一次股东大会，同意设计院有限整体变更为股份有限公司。同日，立信会计师事务所（特殊普通合伙）对本次整体变更有关出资事宜进行了审验，并出具《验资报告》（信会师报字[2018]第ZC10449号），确认全体股东已经完成出资。2018年8月29日，发行人就本次整体变更事项完成工商变更登记，并取得广州市工商局核发的《营业执照》，公司名称由“广州地铁设计研究院有限公司”变更为“广州地铁设计研究院股份有限公司”。2019年2月3日，广东省国资委下发《关于广州地铁设计研究院股份有限公司国有股权管理方案的批复》（粤国资函[2019]162号），同意发行人的国有股权管理方案，确认广州地铁集团、广东铁投、越秀集团、广州国发、广州金控所持发行人股份性质为国有法人股。', '广东省广州市越秀区环市西路204号', '华南', '广东', '广州市', '1993-08-06', '40001', '正常上市', '2020-10-22', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300284', '苏交科', '交通工程咨询与工程承包业务，交通项目前期咨询及科研，道路、桥梁、铁路与轨道交通、岩土与隧道工程、水运工程与市政工程的勘察、设计、咨询、试验检测、监理、相关技术服务，工程总承包及其他承包业务等服务。', '国内外房屋建筑业、土木工程建筑业、建筑安装业、生态保护和环境治理业、软件和信息技术服务业的总承包；上述项目的地质勘察、工程技术、施工、环境影响评价、研究和试验发展；质检技术服务，科技中介服务，环境与生态监测；建筑材料、建筑材料生产专用机械、机动车辆、建筑工程专用机械的开发、制造、生产、销售；实业投资与资产管理；贸易经纪与代理，机械设备、五金产品及电子产品批发，建材批发；设计、制作印刷品广告；司法鉴定，社会经济咨询，经济信息咨询服务。（依法须经批准的项目，经相关部门批准后方可开展经营活动）一般项目：工程造价咨询业务（除依法须经批准的项目外，凭营业执照依法自主开展经营活动）', '公司前身交科有限成立于2002年8月29日。2008年8月25日，交科有限召开第三十三次股东会，全体股东一致同意以交科有限截至2008年6月30日经审计的净资产247,796,817元为基数，以整体变更的方式共同发起设立江苏省交通科学研究院股份有限公司，股本总额为180,000,000元，剩余67,796,817元作为资本公积金。同日，公司发起人召开创立大会暨第一次股东大会，一致同意设立江苏省交通科学研究院股份有限公司，并审议通过了公司章程、选举了第一届董事会董事和第一届监事会中由股东代表出任的监事。会计师为此出具了天衡验字（2008）71号《验资报告》。2008年9月12日，苏交科取得了江苏省工商行政管理局核发的注册号为320000000046386的《企业法人营业执照》，依法登记成立。', '江苏省南京市水西门大街223号', '华东', '江苏', '南京市', '2002-08-29', '126282.7774', '正常上市', '2012-01-10', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300492', '华图山鼎', '建筑工程设计及相关咨询服务。', '建筑工程及园林设计、相关业务咨询(凭建设部门资质证书经营）；货物进出口；技术进出口；代理进出口；工程项目管理、建筑工程施工总承包及所需建筑材料和设备的采购和销售。（以上经营范围不含国家法律、行政法规、国务院决定禁止或限制的项目，依法须批准的项目，经相关部门批准后方可开展经营活动）。', '本公司系由四川山鼎建筑工程设计咨询有限公司整体变更设立的股份公司。2011年6月28日，山鼎有限股东会做出决议，决定以发起设立方式将山鼎有限整体变更为股份公司。同日，山鼎有限全体股东签署了《发起人协议书》，决定以山鼎有限截至2011年4月30日净资产73,210,565.80元中的62,400,000.00元折合股份有限公司的股本总额62,400,000股，其余10,810,565.80元列入股份公司的资本公积。2011年8月18日，山鼎设计在成都市锦江工商行政管理局登记注册，并取得了成都市锦江工商行政管理局核发的营业执照，注册号为：510000000009008。', '四川省成都市锦江区东大街芷泉段6号1栋1单元37层8号', '西南', '四川', '成都市', '2003-02-13', '14048.647', '正常上市', '2015-12-23', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300500', '启迪设计', '设计业务、绿色建筑节能整体技术服务、工程总承包、工程检测。', '建筑工程及相应的工程咨询和装饰设计，智能建筑（系统工程设计），建筑工程勘察，土建设计、电力（变电工程设计）、市政工程（道路、桥隧、排水设计）、园林工程设计、轨道交通设计；城乡规划编制；工程造价咨询；全过程工程咨询；工程项目管理、建筑工程施工总承包及所需建筑材料和设备的销售；从事建筑学、土木工程的技术研究、开发、转让、建筑监理（乙级）；晒图、模型制作、提供建筑学、土木工程建设方面的技术咨询、电算工程测试服务。（以上项目涉及资质的凭资质证书经营）（依法须经批准的项目，经相关部门批准后方可开展经营活动）', '公司系根据苏州有限截至2012年2月29日经审计的净资产折股整体变更设立的股份有限公司。公司于2012年5月31日取得江苏省工商行政管理局核发的《企业法人营业执照》（注册号为320500000030233）。立信会计师事务所（特殊普通合伙）出具《验资报告》（信会师报字[2012]第113214号）：公司设立时的注册资本为4,500万元人民币。', '江苏省苏州工业园区星海街9号', '华东', '江苏', '苏州市', '1988-03-03', '17413.9024', '正常上市', '2016-02-04', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300564', '筑博设计', '建筑设计及相关业务的设计与咨询。', '建筑工程及工程咨询和装饰设计；房屋租赁。', '公司的前身为筑博有限。1996年3月15日，深圳市建设局出具《关于同意成立深圳市筑博工程设计有限公司的批复》（深建复【1996】52号），同意金路工贸、徐先林、李兵与徐江共同投资100万元成立筑博有限，经营建筑工程设计业务。其中：金路工贸出资15万元，徐先林出资50万元，李兵出资25万元，徐江出资10万元。1996年3月19日，深圳市中平会计师事务所出具中平验字（96）第057号《验资报告》，验证截止1996年3月19日筑博有限的实收资本为100万元，出资各方已缴足各自认缴资本。1996年3月25日，筑博有限在深圳市工商局完成注册登记，并领取了深圳市工商局核发的《企业法人营业执照》（注册号为19242082-6号）。', '西藏自治区拉萨市柳梧新区国际总部城6幢2单元6层1号', '西南', '西藏', '拉萨市', '1996-03-25', '10293', '正常上市', '2019-11-08', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300635', '中达安', '工程监理业务为主的项目管理服务。', '工程监理、承包、代建，工程项目管理，室内装修，工程造价咨询服务，工程设计及咨询服务，工程质量检验、检测及监督，工程安全检测服务，建筑智能工程检测；招标代理，政府采购代理；建筑、通信材料及设备购销；建筑信息模型管理技术的咨询服务；信息网络系统集成，软件的开发、销售及转让，计算机信息咨询服务；建筑、通信、计算机的技术咨询、技术开发、技术转让、技术服务、技术培训、技术承包、技术中介、技术入股；项目投资、咨询及管理；企业管理咨询及培训，法律咨询服务；通信技术、采购的管理咨询及培训；会务服务；企业管理、工程管理的中介服务；工程咨询服务；路灯设备、通信设备、传输管线、传输设备、安防产品、机房设备及网络设备的购销、安装及维护工程；路灯及通信基础设施投资及租赁；为国外投资者提供投资方面的信息服务；工程结算服务。(依法须经批准的项目，经相关部门批准后方可开展经营活动)。', '发行人的前身达安有限成立于1998年12月11日。2012年11月16日，达安有限以经亚太（集团）会计师事务所有限公司广东分所审计的截至2012年7月31日的净资产额99,554,340.56元为基础，按照1.8099:1的比例折合股本5,500.00万股，整体变更为股份有限公司。本次整体变更后，达安有限更名为“广东达安项目管理股份有限公司”，广东省工商行政管理局核发了新的《企业法人营业执照》，注册号为44000000008161，注册资本为5,500.00万元。达安有限整体变更为股份公司后，2013年10月12日，本公司以增资方式引入员工持股公司广州鑫胜，公司注册资本增至5,844.40万元；2013年11月6日，本公司以增资方式新增1名自然人股东黄亮（认缴注册资本15.00万元），同时公司原自然人股东认缴新增注册资本140.60万元，公司注册资本变更为6,000.00万元；2015年6月30日，公司以增资方式新增3名外部股东，平安天成、智汇天成和尹文飞认缴新增注册资本360.00万元，公司注册资本变更为6,360.00万元。', '广东省广州市白云区广州大道北1421号602房(部位；自编636房)', '华南', '广东', '广州市', '2000-08-08', '13632.2', '正常上市', '2017-03-31', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300668', '杰恩设计', '建筑室内设计服务。', '室内装饰、建筑幕墙、园林景观、空调工程、强弱电工程、灯光照明、给排水工程的设计与咨询（取得行业行政主管部门颁发资质证书后方可开展经营）；室内摆设设计，家具的设计和销售（以上不含限制项目和专营、专控、专卖商品）；货物及技术进出口（但国家限制公司经营或禁止进出口的商品和技术除外）；计算机软硬件的技术开发与销售；弱电工程、网络布线、计算机系统集成服务。', '本公司系由姜峰、冉晓凤、袁晓云、宋越以及非法人企业杰创汇鑫、佳创汇鑫等6名姜峰有限全体股东为发起人，以姜峰有限截至2015年3月31日经立信会计师事务所（特殊普通合伙）审计的净资产折股整体变更设立，公司设立时的注册资本为3,080万元。2015年6月25日，立信会计师事务所（特殊普通合伙）出具了《验资报告》（信会师报字[2015]第151326号）验证：公司注册资本3,080万元已足额到位。2015年7月27日，公司取得了深圳市市场监督管理局核发的《营业执照》（统一社会信用代码：9144030076755763XT）。', '广东省深圳市南山区粤海街道科苑路15号科兴科学园B4单元13楼', '华南', '广东', '深圳市', '2004-10-11', '12038.1273', '正常上市', '2017-06-19', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300675', '建科院', '建筑设计、绿色建筑咨询、生态城市规划、公信服务等业务。', '城市及建筑科学研究，城市规划编制，工程咨询、勘察、设计、质量检测与检查、项目管理、监理及相关技术服务，环境工程检测和咨询，建筑工程性能评估，能耗测评及节能检测评价，绿色节能改造咨询与施工，绿色建筑与园区运营管理，碳审计与评估，绿色低碳技术与产品开发、咨询、投资、培训推广和销售贸易，会议展览，物业租赁与管理,建筑服务。绿色低碳技术培训推广。', '根据2013年11月28日召开的建科院有限股东会决议，建科院有限以2013年5月31日为改制基准日，整体变更设立股份公司，各股东以其所拥有的截至2013年5月31日建科院有限的净资产166,322,358.71元，按1：0.6614的比例折合股份总额11,000万股，大于股本部分计入资本公积。上述出资已经立信会计师事务所（特殊普通合伙）出具的信会师报字[2013]验字第310546号《验资报告》验证。公司已于2013年12月25日在深圳市市场督管理局办理了股份公司设立的工商变更登记手续。', '广东省深圳市福田区梅林梅坳三路29号建科大楼', '华南', '广东', '深圳市', '2007-08-20', '14666.67', '正常上市', '2017-07-19', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300732', '设研院', '咨询、规划、勘察、设计、测绘、试验、检测、监测、监理、项目管理等工程咨询服务。', '许可项目：工程造价咨询业务；国土空间规划编制；建设工程勘察；建设工程设计；测绘服务；建设工程质量检测；水利工程质量检测；房屋建筑和市政基础设施项目工程总承包；地质灾害治理工程勘查；地质灾害治理工程设计；地质灾害危险性评估（依法须经批准的项目，经相关部门批准后方可开展经营活动，具体经营项目以相关部门批准文件或许可证件为准）。一般项目：工程管理服务；规划设计管理；公路水运工程试验检测服务；技术服务、技术开发、技术咨询、技术交流、技术转让、技术推广；对外承包工程；环保咨询服务；水环境污染防治服务；土壤污染治理与修复服务；新兴能源技术研发；土地调查评估服务；信息技术咨询服务；软件开发；信息系统集成服务；专业设计服务；摄像及视频制作服务；数字内容制作服务（不含出版发行）；环境保护监测；建筑材料销售、新型建筑材料制造(不含危险化学品)（除依法需经批准的项目外，凭营业执照依法自主开展经营活动）。', '发行人系河南省交通规划勘察设计院有限责任公司整体变更设立的股份有限公司。2015年8月19日，交设院有限召开股东会，同意交设院有限整体变更为股份有限公司。根据瑞华会计师事务所出具的《审计报告》（瑞华审字[2015]41030034号）、上海申威资产评估有限公司出具的《资产评估报告》（沪申威评报字[2015]第HN0008号），以有限公司截至2015年6月30日经审计的净资产人民币391,822,792.49元为基数，按1：0.138的比例折股，折为股份公司5,400万股，每股面值1元，差额337,822,792.49元计入公司资本公积。2015年9月22日，瑞华会计师事务所对拟设立股份公司的注册资本进行审验并出具《验资报告》（瑞华验字[2015]41030019号）。2015年10月12日，发行人取得郑州市工商行政管理局颁发的《营业执照》，统一社会信用代码为91410100706774868X。', '河南省郑州市郑东新区泽雨街9号', '华中', '河南', '郑州市', '2007-08-22', '27406.3336', '正常上市', '2017-12-12', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300746', '汉嘉设计', '建筑设计、装饰景观市政设计及EPC总承包等。', '建筑工程设计，室内外装饰工程设计，园林工程设计，建设工程总承包（凭资质证书经营）、技术研究、开发、服务，经济技术咨询，晒图。（依法须经批准的项目，经相关部门批准后方可开展经营活动）', '本公司前身为成立于1993年2月23日的浙江城建建筑设计所，1998年6月16日改制为浙江城建建筑设计院有限公司。2007年3月22日，浙江城建设计集团股份有限公司召开创立大会，以有限公司截至2007年1月31日经审计的净资产248,977,479.21元折成1亿股，剩余净资产计入资本公积后整体变更设立股份有限公司。2007年3月22日，浙江天健会计师事务所有限公司对有限公司整体变更设立股份公司的注册资本和实收资本变更情况进行了审验，并出具了【浙天会验（2007）第21号】《验资报告》，确认本次出资已经足额到位。2007年3月28日，浙江城建设计集团股份有限公司向浙江省工商行政管理局办理了工商变更登记手续，并取得了注册号为【3300001001602】的《企业法人营业执照》。2008年4月8日公司更名为汉嘉设计集团股份有限公司。', '浙江省杭州市湖墅南路501号迪尚商务大厦', '华东', '浙江', '杭州市', '1998-06-16', '22573.8328', '正常上市', '2018-05-25', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300778', '新城市', '各级政府部门、知名企业和城乡社区的城乡规划、工程设计、工程咨询等专业服务。', '规划、建筑、市政道桥设计；室内装饰工程设计；工程咨询；自有物业租赁。', '发行人前身是于2002年7月9日成立的深圳市龙规院规划建筑设计有限公司。公司成立时注册资本为120万元，由工会委员会、张春杰、宋波分别以货币出资68.40万元、36.00万元以及15.60万元，占注册资本的57.00%、30.00%以及13.00%。2002年5月29日，深圳天华会计师事务所有限公司出具“验变[2002]50号”《验资报告》，对此次出资情况予以验证。2002年7月10日，深圳市工商行政管理局核准企业名称变更为“深圳市龙规院规划建筑设计有限公司”。2003年9月3日，深圳市工商行政管理局出具《变更通知书》，并核发新的《企业法人营业执照》（注册号：4403011005971），公司名称由“深圳市龙规院规划建筑设计有限公司”变更为“深圳市新城市规划建筑设计有限公司”。2016年12月3日，新城市有限召开发起人会议，全体发起人签署了《深圳市新城市规划建筑设计有限公司关于整体变更设立股份有限公司的股东会决议》，一致同意新城市有限整体变更为新城市股份。2016年12月18日，全体发起人召开了股份公司创立大会，并签署了《深圳市新城市规划建筑设计股份有限公司创立大会决议》。根据立信出具的“信会师报字[2016]第310958”号《审计报告》，新城市有限以截止2016年8月31日经审计后的净资产135,263,470.72元为基数折为6,000万股，每股面值1元人民币，净资产超过股本总额的部分计入新城市股份资本公积。立信会计师事务所（特殊普通合伙）对公司注册资本到位情况进行了验证，并出具了“信会师报字[2016]310971号”《验资报告》验证。2016年12月28日，新城市领取了统一社会信用代码号为914403002795212083的《营业执照》。', '广东省深圳市龙岗区中心城清林中路39号新城市大厦10楼', '华南', '广东', '深圳市', '1993-03-22', '12500', '正常上市', '2019-05-10', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300826', '测绘股份', '地理信息产业中的测绘地理信息服务业务。', '测绘与地理信息服务，自然资源调查、评估与监测服务；软件开发及系统集成；工程勘察；交通工程检测、监测；对外承包工程；设计、制作、代理、发布国内各类广告（许可证经营项目除外）；测绘产品检测；地基基础检测；深基坑支护工程监测；变形观测、岩土测试；管道腐蚀检测；测绘仪器销售、维修；国土空间规划服务；风景园林、道路交通和市政工程设计与咨询；岩土工程设计；建筑设计、咨询；规划设计、咨询；石油勘探；地质灾害治理工程勘察、设计；污染场地调查评估、修复方案设计、修复工程实施、修复项目监理、修复验收、分析检测；排水防涝设施检测；管道内窥镜检测及管网信息系统开发、维护；地籍检测；地下管线探测；排水管道检测（内窥镜、CCTV）、清疏、修复、养护；排水设施检测；水质检测；测绘与地理信息咨询服务；物业管理；停车场管理服务；地图零售；其他印刷品印刷。（依法须经批准的项目，经相关部门批准后方可开展经营活动）。一般项目：环保咨询服务（除依法须经批准的项目外，凭营业执照依法自主开展经营活动）', '2017年3月4日，测绘有限召开股东会，就公司整体变更为股份有限公司的相关事宜一致通过决议：同意以致同会计师事务所（特殊普通合伙）出具的《审计报告》（致同审字[2017]第320ZA0009号）为依据，以净资产33,400.629415万元，按照5.5668：1的比例折股，变更后股份公司的股份总数为6,000万股，每股面值人民币一元，注册资本为人民币6,000万元；并解散持股会，持股会代各位会员持有的全部股权，由各会员直接持有。2017年3月19日，南京高投等183名发起人签署了《南京市测绘勘察研究院股份有限公司发起人协议》，以各自持有的有限公司股权，按截至2016年9月30日之审计账面净资产，折合成股份公司股份。2017年3月19日，致同会计师事务所（特殊普通合伙）出具了《验资报告》（致同验字[2017]第320ZA0004号），验证：截至2017年3月19日，公司已将截至2016年9月30日止经审计的净资产33,400.629415万元折合股份6,000万股（每股面值人民币一元），其中6,000万元作为注册资本，其余27,400.629415万元作为资本公积。同日，股份公司召开创立大会审议通过了：设立南京市测绘勘察研究院股份有限公司、股份公司筹办情况及筹办费用开支情况的报告、股份公司三会议事规则、选举公司董事、监事等十五项议案。此后召开的公司第一届董事会第一次会议选举了董事长、聘任了公司总经理及其他高级管理人员；第一届监事会第一次会议选举了监事会主席。2017年3月28日，公司完成股份公司设立的工商登记，并领取了变更登记后的《营业执照》。', '江苏省南京市建邺区创意路88号', '华东', '江苏', '南京市', '1991-04-08', '11200', '正常上市', '2020-04-03', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300844', '山水比德', '社区景观、文化旅游、商业空间、市政公共等领域的整体性解决方案业务。', '旅游景区规划设计、开发、管理;风景园林工程设计服务;城市规划设计;园林、陈设艺术及其他陶瓷制品制造;房屋建筑工程设计服务;市政工程设计服务;建筑材料设计、咨询服务;雕刻艺术创作服务; (依法须经批准的项目，经相关部门批准后方可开展经营活动)', '山水比德系由山水有限以整体变更的方式设立。2017年2月13日，山水有限召开股东会，决议以2016年11月30日为审计和评估基准日由有限责任公司整体变更为股份有限公司。截至2016年11月30日，山水有限经审计净资产为13,237,828.10元，净资产评估价值为14,043,556.47元。山水有限以截至2016年11月30日经审计净资产13,237,828.10元为基础，折合股本1,000万股，每股面值1.00元，股本与净资产差额3,237,828.10元转入资本公积。就上述事项，立信事务所验证截至2017年2月13日止，山水比德注册资本和账面实收股本均为10,000,000.00元，出资方式为净资产。2017年2月20日，山水比德在广州市工商行政管理局完成工商变更登记，取得变更后的《营业执照》，统一社会信用代码为91440106799435733E，注册资本1,000万元。', '广东省广州市海珠区新港东路1166号201房,202房,203房,204房,205房,301房（部位:自编A),302房,303房,304房', '华南', '广东', '广州市', '2007-04-09', '4040', '正常上市', '2021-08-13', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300977', '深圳瑞捷', '建设工程第三方评估、管理和咨询服务。', '建设工程质量、安全等第三方评估与咨询服务；项目管理；工程管理、工程咨询；企业管理咨询；提供建筑材料评估服务；城市建设及配套的质量、安全评估服务；电子信息技术、高新技术服务；计算机软件研发与销售、技术转让；建设工程的数据平台软件研发与销售。建设工程质量检测、环境检测、环境监测、产品质量检测；消防技术咨询服务、安全生产咨询服务，消防工程咨询与技术服务。（以上项目法律、行政法规、国务院决定禁止及限制的项目除外，依法须经批准的项目须取得许可后方可经营）；消防设施检测、消防安全评估。', '2018年10月12日，瑞捷有限召开股东会并通过决议，同意以整体变更方式发起设立为股份公司。经审计确认，截至2018年7月31日，瑞捷有限的净资产为85,791,112.31元。北京北方亚事资产评估事务所（特殊普通合伙）出具《深圳市瑞捷建筑工程咨询有限公司拟整体变更为股份有限公司所涉及的公司账面净资产项目资产评估报告》（北方亚事评报字[2018]第01-500号），确认截至评估基准日2018年7月31日，瑞捷有限净资产的评估值为8,591.95万元。2018年10月30日，公司全体发起人范文宏、黄新华、瑞皿投资、瑞可投资、瑞宏捷、瑞华捷签署了《发起人协议》，约定以瑞捷有限截至2018年7月31日经审计的净资产85,791,112.31元为基础，折成33,600,000股（每股面值1.00元），其余52,191,112.31元计入股份公司资本公积，整体变更设立股份公司，瑞捷有限股东按其在瑞捷有限的持股比例相应折为其在股份公司的发起人股份。2018年10月31日，公司召开创立大会暨第一次股东大会，审议通过了设立股份公司的相关议案，并选举产生了公司第一届董事会成员及第一届监事会非职工代表监事。根据《验资报告》（广会验字[2018]G17035340069)，截至2018年11月23日，全体发起人已以瑞捷有限截至2018年7月31日经审计的净资产8,579.11万元中的3,360.00万元折为股份公司股本3,360.00万股，未折股的净资产计入股份公司的资本公积。2018年11月23日，公司取得深圳市市场监督管理局核发的《营业执照》（统一社会信用代码：91440300553899896Q)，瑞捷有限变更为股份有限公司，法定代表人为黄新华，注册资本3,360万元。', '广东省深圳市龙岗区坂田街道雅宝路1号A栋A3101、A3103、A3108号', '华南', '广东', '深圳市', '2010-04-12', '6837.89', '正常上市', '2021-04-20', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300983', '尤安设计', '建筑设计业务的研发、咨询与技术服务。', '建筑专业建筑工程设计，建筑装饰工程设计、建筑幕墙工程设计、轻型钢结构工程设计、建筑智能化系统设计、照明工程设计和消防设施工程设计。【依法须经批准的项目，经相关部门批准后方可开展经营活动】', '2016年3月18日，正轶有限通过股东会决议：以正轶有限截至2015年12月31日经大华会计师审计的净资产58,531,136.13元为基准，整体折股变更为股份有限公司。原有限公司的全体股东作为股份公司发起人按其在原有限公司中的持股比例认购全部股份。2016年4月2日，正轶有限全体股东共同签署了《发起人协议书》。2016年4月2日，大华会计师出具《验资报告》（大华验字[2016]000249号），截至2016年4月2日，公司注册资本2,200.00万元已足额到位。2016年4月13日，正轶股份办理了相应的工商变更登记手续并领取了换发的《营业执照》（统一社会信用代码：91310110757926286X)。', '上海市宝山区一二八纪念路968号1618室', '华东', '上海', '上海市', '2004-01-06', '8000', '正常上市', '2021-04-20', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('300989', '蕾奥规划', '规划设计业务、工程设计业务。', '区域与城乡规划设计；城市设计；风景园林规划与设计；旅游策划与规划；交通规划设计；园区运营与管理咨询；城市信息系统研究与应用咨询。', '2016年9月15日，有限公司召开股东会并作出决议，同意以2016年7月31日为基准日将深圳市蕾奥城市规划设计咨询有限公司整体变更为深圳市蕾奥规划设计咨询股份有限公司；同意以2016年7月31日为改制基准日经审计的账面净资产值20,740,324.37元按1.0370:1的比例折为20,000,000股，每股面值1元，注册资本2,000.00万元，余额740,324.37元计入资本公积。2016年9月15日，有限公司股东签订了《发起人协议》，就拟将有限公司整体变更为股份公司、股份公司的名称与住所、经营期限、经营范围与目的、注册资本与股本结构、发起人的权利和义务、发起人组织机构的设置等内容作出了明确约定。2016年9月15日，亚太（集团）会计师事务所（特殊普通合伙）出具亚会B审字(2016)1729号《审计报告》，对有限公司的资产负债情况、利润情况进行审计。经审计，截至2016年7月31日，有限公司的净资产为20,740,324.37元。2016年9月18日，国众联资产评估土地房地产估价有限公司出具国众联评报字(2016)第2-802号《评估报告》，对有限公司整体变更并设立为股份有限公司所涉及的全部资产和负债进行评估。经评估，于2016年7月31日，有限公司的净资产评估值为2,530.22万元。2016年9月30日，全体发起人召开了股份公司创立大会暨第一次股东大会，并签署了《深圳市蕾奥规划设计咨询股份有限公司创立大会暨第一次股东大会决议》。亚太（集团）会计师事务所（特殊普通合伙）对公司股东出资情况进行审验，并出具亚会B验字(2016)0671号《验资报告》。审验确认，截至2016年7月31日，公司已收到全体出资者经审计的净资产20,740,324.37元，根据《公司法》的有关规定，按照公司的折股方案，将上述净资产折合股本2,000.00万元，资本公积740,324.37元。2016年10月25日，公司在深圳市市场监督管理局注册登记，统一社会信用代码：914403006748035555，法定代表人为王富海，注册资本为2,000万元。', '广东省深圳市福田区莲花街道景华社区景田综合市场A座406', '华南', '广东', '深圳市', '2008-05-07', '6000', '正常上市', '2021-05-07', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301024', '霍普股份', '建筑专业领域内的咨询顾问服务。', '一般项目：建筑工程设计，装饰设计，环境景观设计，建筑专业领域内的技术咨询、技术服务，室内装饰及室内陈设设计，装饰用品销售，动画设计、动漫设计、图文设计，建筑装修装饰工程，计算机软硬件的技术开发与销售，企业管理咨询，货物及技术进出口（但国家限制公司经营或禁止进出口的商品和技术除外）。（除依法须经批准的项目外，凭营业执照依法自主开展经营活动）', '2015年4月27日，霍普有限召开股东会并作出决议，同意由霍普控股、龚俊、赵恺、成立作为发起人，以发起设立方式将霍普有限整体变更为股份有限公司，同意以经立信出具《审计报告》（信会师报字[2015]第113200号）审定的霍普有限截至2015年3月31日的净资产8,601.19万元为基准，折合股本3,000.00万元，其余5,601.19万元计入资本公积。根据立信出具的《验资报告》（信会师报字[2015]第151116号），截至2015年5月18日，霍普有限已将其截至2015年3月31日经审计的净资产折合为股本3,000.00万股，公司的注册资本已足额缴纳。2015年5月28日，霍普股份完成本次工商设立登记手续，依法取得了上海市工商行政管理局核发的《营业执照》（注册号：310115001076175)。公司系发起设立的股份有限公司；在上海市市场监督管理局登记注册，取得营业执照，统一社会信用代码为913100006762867235。', '上海市浦东新区民生路3号60幢270库二层', '华东', '上海', '上海市', '2008-06-24', '4239', '正常上市', '2021-07-28', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301027', '华蓝集团', '工程设计、国土空间规划、工程总承包管理及工程咨询。', '对建筑业的投资与管理；国内贸易；企业总部管理；房地产租赁经营；文化会议服务；工程管理服务。（依法须经批准的项目，经相关部门批准后方可开展经营活动。）', '2012年4月6日，雷翔、赵成、吴广意等150名自然人签署了《发起人协议书》，约定共同发起设立广西华蓝控股集团股份公司，注册资本10,000.00万元，均以货币形式出资。2012年4月6日，华蓝控股召开股东大会，审议通过了《公司章程》，并选举了第一届董事会、监事会成员。2012年4月18日，雷翔、赵成、吴广意等150名自然人股东签署了《公司章程》。2012年5月9日，华蓝控股取得了自治区工商局核发的注册号为450000200036461的《企业法人营业执照》，注册资本为10,000.00万元，法定代表人为雷翔。统一社会信用代码为9145000059686217XU。', '广西壮族自治区南宁市青秀区月湾路1号华蓝弈园', '华南', '广西', '南宁市', '2012-05-09', '14700', '正常上市', '2021-07-15', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301038', '深水规院', '水务建设工程及其配套工程的专业技术服务。', '一般经营项目是：水利工程、市政工程、水力发电工程、建筑工程、园林景观工程的科研、咨询、勘察、测量、设计；水利工程质量检测；水文水资源调查、论证；晒图；复印、打印（法律、行政法规、国务院决定禁止的项目除外，限制的项目须取得许可后方可经营）；计算机系统集成、计算机技术服务及技术咨询、计算机网络工程。许可经营项目是：污染治理设施运行服务；地质灾害治理工程勘查、设计；地质灾害危险性评估；建设工程总承包及项目管理和相关的技术与管理服务。', '2018年8月31日，立信会计师事务所（特殊普通合伙）出具的《审计报告》（信会师报字[2018]第ZI10606号）确认，深水规院有限截至2018年2月28日的账面净资产为39,970.39万元。2018年12月16日，北京中企华资产评估有限责任公司出具的《资产评估报告》（中企华评报(2018)4648号）确认，深水规院有限截至2018年2月28日的净资产评估值为44,530.95万元。2018年12月21日，深水规院有限召开2018年第三次临时股东会，审议通过了公司按照经审计账面净资产扣除向股东分红后金额折股整体变更为股份公司的议案。2018年12月28日，深水规院召开创立大会作出决议，同意水规院截至2018年2月28日经审计的账面净资产扣除向股东分红后金额382,190,734.98元以1:0.2590的比例折股整体变更设立股份公司，股本总额为9,900万股普通股，每股面值1元，剩余283,190,734.98元计入资本公积。2019年1月14日，立信会计师事务所（特殊普通合伙）出具《验资报告》（信会师报字[2019]第ZI20001号）对上述出资进行审验确认。2019年1月16日，深水规院取得了换发的《营业执照》（统一社会信用代码：91440300672999996A)，股份总额为9,900万股。', '广东省深圳市龙华区民治街道龙塘社区星河传奇花园三期商厦1栋C座1110', '华南', '广东', '深圳市', '2008-04-03', '13200', '正常上市', '2021-08-04', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301058', '中粮工科', '工程设计咨询、机电工程系统交付和设备制造。', '建筑工程、机电工程、防水防腐保温工程、钢结构工程、建筑机电安装工程的设计、施工、维护；工程项目管理服务；工程技术咨询；工程造价咨询；工程招标代理；粮油、食品、饲料专用机械设备、自控设备、食品添加剂、饲料添加剂的技术开发、技术咨询、技术转让、技术服务、制造及销售；国内贸易（不含国家限制及禁止类项目）；压力容器和压力管道设计；食品、生物制品、化工产品、轻工产品的研发；食品生产；专用设备、机械零部件的制造、销售及安装；出版物发行；设计、制作、代理、发布各类广告业务；自有房屋、机械设备租赁（不含融资性租赁）；自营和代理各类商品及技术的进出口业务（但国家限定公司经营或禁止进出口的商品和技术除外）。', '2017年7月30日，工科有限通过第二届董事会第四次会议决议，同意工科有限由有限责任公司整体变更为股份有限公司，同意股份有限公司的名称为“中粮工程科技股份有限公司”，股份制改革的审计和评估基准日均为2017年8月31日，股份有限公司的注册资本以截至2017年8月31日经审计的净资产值为折股依据。2017年8月21日，中粮集团出具《关于中粮工科改制重组项目的批复》（中粮总字[2017]185号），同意工科有限由公司制企业改造为股份制企业，并更名为“中粮工程科技股份有限公司”。2018年1月23日，信永中和出具《中粮工程科技有限公司2015年1月1日至2017年8月31日审计报告》(XYZH/2018QDA10010)，根据该审计报告，截至2017年8月31日，工科有限的股东权益合计970,486,795.65元。2018年3月29日，东洲评估出具《资产评估报告》（东洲评报字[2018]第0085号），根据该评估报告，工科有限截至评估基准日2017年8月31日的股东全部权益价值为1,210,000,000元。根据《国有资产评估项目备案表》（备案编号：2838ZLJT2018073)，该评估报告已于2018年8月9日经中粮集团备案。2018年7月13日，工科有限召开股东会并通过《股东会决议》，同意工科有限由有限责任公司整体变更为股份有限公司，原工科有限的全体股东即为股份有限公司的全体发起人，股份有限公司的注册资本为358,762,356元，总股本为358,762,356股，同意有限责任公司变更为股份有限公司的审计和评估基准日均为2017年8月31日。2018年8月15日，工科有限股东签署《中粮工程科技股份有限公司发起人协议》。2018年8月15日，工科有限召开创立大会并通过《创立大会决议》，同意全体股东按照其各自持股比例拥有工科有限净资产额的相应份额，按照1:0.369672578的折股比例全部折为358,762,356股，并划分为等额股份，股份类型为人民币普通股，每股面值人民币1元，折股后总股本为358,762,356股，剩余人民币611,724,439.65元计入股份有限公司资本公积，将公司整体变更为股份有限公司，并审议通过《中粮工程科技股份有限公司章程》。2018年8月16日，信永中和出具《验资报告》(XYZH/2018QDA10448)，经审验，截至2018年8月15日，中粮工程科技股份有限公司（筹）已收到各股东以其拥有的工科有限截至2017年8月31日经审计后的净资产折合的股本合计人民币358,762,356元。2018年8月28日，无锡市行政审批局向中粮工科核发《营业执照》（统一社会信用代码：91320211100013394P)。', '江苏省无锡市惠河路186号', '华东', '江苏', '无锡市', '1993-03-31', '51227.4245', '正常上市', '2021-09-09', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301091', '深城交', '规划咨询、工程设计与检测、大数据软件与智慧交通业务。', '交通规划设计研究（含专项调查）；市政公用工程规划设计咨询与设计审查；轨道交通规划设计咨询；智能交通系统规划设计开发运营；城市规划与建筑设计；电子设备、计算机软件的技术开发与销售；计算机信息系统集成；招标代理；会务服务；自有物业租赁。（企业经营涉及前置性行政许可的，须取得前置性行政许可文件后方可经营）', '发行人是由交通有限整体变更设立的股份有限公司。2019年8月15日，中审亚太出具《审计报告》（中审亚太审字[2019]020637号），根据该报告，交通有限截至2019年2月28日的净资产为177,900,576.12元。2019年9月3日，交通有限召开股东会2019年第四次临时会议，审议通过了《关于公司整体变更为股份公司的议案》。全体股东一致同意以发起方式将公司由有限责任公司整体变更为股份有限公司。2019年9月20日，天健兴业出具《深圳市城市交通规划设计研究中心有限公司拟整体变更为股份有限公司而涉及该公司净资产项目评估报告》（天兴评报字[2019]第0839号），根据该报告，交通有限截至2019年2月28日的净资产评估值为27,124.18万元。2019年11月20日，深智城对该资产评估结果予以备案。2019年11月6日，深智城出具《关于深圳市城市交通规划设计研究中心有限公司股改工作相关事项的批复》（深智城司[2019]22号），同意交通有限召开创立大会、第一届董事会和监事会会议，并提名了董事、监事候选人。2019年11月15日，发起人深智城、深研投资、启迪控股、北京联想和高瓴道远共同签署《深圳市城市交通规划设计研究中心股份有限公司发起人协议》，一致同意以发起方式设立发行人，以公司截至审计基准日2019年2月28日的账面净资产折股，其中12,000万元折合为发行人的实收股本，每股面值1元，超出股本总额部分的57,900,576.12元计入资本公积。2019年12月8日，中审亚太对各发起人投入发行人的资产进行验证并出具《验资报告》（中审亚太验字[2019]020637-1号），验证各发起人出资额已按时足额缴纳。同日，发行人召开创立大会，同意以发起方式设立发行人。2019年12月17日，深圳市市监局向发行人核发了统一社会信用代码为91440300671877217N的《营业执照》。', '广东省深圳市龙华区民治街道龙塘社区星河传奇花园三期商厦1栋C座1210', '华南', '广东', '深圳市', '2008-01-14', '16000', '正常上市', '2021-10-29', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301136', '招标股份', '工程监理、试验检测、招标服务、测绘与地理信息服务、勘察设计。', '从事各类招标代理、工程项目建设中的项目策划、投资咨询（不含证券、期货、保险）、工程监理、项目管理等服务；工程勘察设计；工程试验检测；工程测量、房产测绘服务；房地产价格评估；艺术品评估；数据处理和存储服务；价格评估。（依法须经批准的项目，经相关部门批准后方可开展经营活动）', '2015年11月18日，招标集团向福建省国资委报送《福建省招标采购集团有限公司关于报送公司整体上市方案的请示》（闽招采文[2015]109号），申请批复《福建省招标采购集团有限公司整体改制上市方案》（以下简称“上市方案”）。2016年1月23日，福建省人民政府对福建省国资委转报的《关于福建省招标采购集团有限公司整体改制上市的请示》（闽国资运营[2015]248号）作出批复，原则同意招标集团整体上市总体思路。2016年2月2日，福建省国资委出具《福建省人民政府国有资产监督管理委员会关于同意福建省招标采购集团有限公司开展整体改制上市公司的批复》（闽国资运营[2016]24号），就招标集团上报的《福建省招标采购集团有限公司关于报送公司整体上市方案的请示》作出批复，原则同意招标集团的整体上市总体思路。2016年9月21日，华兴会计师出具编号为闽华兴所(2016)专审字G-028号的《审计报告》，以2015年12月31日为审计基准日，对招标集团拟用作出资的资产及负债进行了审计。经审计，本次拟出资的净资产审计值为22,650.87万元。2016年9月21日，北京中企华出具编号为中企华评报字(2016)第3863号的《福建省招标采购集团有限公司拟出资设立股份有限公司项目评估报告》，以2015年12月31日为评估基准日，对招标集团拟出资资产及负债进行了评估。经评估，本次拟出资的净资产评估值为25,933.48万元。2016年11月21日，福建省国资委对该评估报告进行了备案（备案编号：评备(2016)96号）。2016年11月11日，招标集团召开投资决策会议，决定由招标集团和六一八发展作为发起人发起设立招标股份。其中，招标集团以工程咨询主营业务相关的净资产及部分子公司的股权经评估作价出资，六一八发展以现金出资。2016年11月28日，招标集团作出《关于发起设立福建省招标股份有限公司的决定》，同意上述审计、评估结果，并决定由招标集团、六一八发展作为发起人共同发起设立招标股份。招标集团以《福建省招标采购集团有限公司拟出资设立股份有限公司项目评估报告》确定的25,933.48万元净资产作价出资，其中14,850万元计入注册资本（每股1元），剩余出资计入资本公积；六一八发展以现金人民币261.9543万元作为出资，其中150万元计入注册资本，剩余出资计入资本公积。招标股份设立时注册资本为15,000万元，股份总数为15,000万股，每股面值人民币1.00元，其中，招标集团持有14,850万股，持股比例为99.00%；六一八发展持有150万股，持股比例为1.00%。发行人未以招标集团作为上市主体主要原因是发行人设立前，招标集团已经是一家多主业的综合性国有投资集团，除工程咨询服务业务外，还有产业基金、新能源等其他业务板块。为了发行主体专注工程咨询服务主业，招标集团采取以工程咨询服务主业相关股权及业务作价出资的方式新设发行人。2016年12月6日，招标集团与六一八发展签署《发起人协议》，共同发起设立招标股份。2016年12月6日，发起人召开股东大会，同意设立招标股份。2016年12月8日，公司取得了福建省工商行政管理局颁发的《营业执照》（统一社会信用代码证:91350000MA2XU85R9K)。', '福建省福州市鼓楼区洪山园路68号', '华东', '福建', '福州市', '2016-12-08', '27520.482', '正常上市', '2022-01-11', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('301167', '建研设计', '建筑设计、咨询、研发。', '建筑、城乡规划、市政工程、环境景观、室内外装潢设计；工程技术咨询；工程项目管理；图文制作；物业管理；房屋租赁。（依法须经批准的项目，经相关部门批准之后方可开展经营活动）', '根据华普天健会计师事务所（特殊普通合伙）出具的会审字[2017]4002号《审计报告》，截至2017年2月28日，有限公司经审计的净资产为18,096.20万元；中水致远资产评估有限公司出具了中水致远评报字[2017]第020143号《资产评估报告书》，截至2017年2月28日，有限公司经评估的净资产值为22,994.23万元。根据建院有限2017年6月20日的股东会决议和《发起人协议》，各发起人以2017年2月28日为基准日的经审计建院有限净资产出资，将建院有限整体变更为安徽省建筑设计研究院股份有限公司。2017年6月25日，上述出资情况经华普天健会计师事务所（特殊普通合伙）会验字[2017]第4269号《验资报告》审验确认。2017年6月28日，公司召开股份公司创立大会，决议以有限公司截至2017年2月28日经审计并扣除对股东2016年度的现金分红后的净资产值16,460.58万元，按1:0.364507的比例折合为股份有限公司的股本6,000万元，超出股本部分计入公司资本公积。同日，140名委托持股股东与受托持股人王红兵签署了《解除委托持股协议》，至此，公司全体182名股东及其持股情况还原至实际股权状态。2017年6月29日，公司在安徽省工商行政管理局办理了相关工商变更登记手续，并取得股份公司营业执照。统一社会信用代码为91340000485000016Q。', '安徽省合肥经济技术开发区繁华大道7699号', '华东', '安徽', '合肥市', '1992-07-11', '8000', '正常上市', '2021-12-06', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('600629', '华建集团', '工程设计、工程承包、工程技术管理及勘察、信息化服务及销售。', '建设工程设计、建筑专业建设工程设计、水利专业建设工程设计、市政专业建设工程设计；岩土工程勘察、水文地质勘察、工程测量勘察；建筑智能化建设工程设计施工一体化、消防设施建设工程设计施工一体化、建筑装饰装修建设工程设计施工一体化、建筑幕墙建设工程设计施工一体化；建设工程监理服务，建设工程造价咨询，建设工程招标代理，建设工程检测，建设工程审图，信息技术研发、信息技术咨询、信息技术服务，股权投资，实业投资，投资管理，投资咨询，资产经营，资产管理，出版物经营。【依法须经批准的项目，经相关部门批准后方可开展经营活动】', '公司前身系上海石革玻璃厂，1992年7月始进行股份制改组，将原厂净资产折为面值10元的国家股187.99万股；经同年6月首次公开发行，总股份达337.99万股(上市时拆细为3379.9万股)，内部职工股220万股于1994年4月7日上市流通。', '上海市黄浦区西藏南路1368号五层501室', '华东', '上海', '上海市', '1992-06-12', '65594.1412', '正常上市', '1993-02-09', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('603017', '中衡设计', '建筑领域的工程设计、工程总承包、工程监理及项目管理业务。', '主营国内外各类民用建设项目及工业工程项目的工程咨询、工程设计、工程监理、项目管理、工程总承包及所需设备材料的采购和销售；开发区规划与建设咨询；城市规划、市政设计、景观与园林设计；兼营软件研发、图文设计与制作；自有房屋租赁；检验检测；既有建筑的加固与改造。（企业经营范围中涉及行政许可的，凭许可证件经营）。', '本公司是由设计院有限依法整体变更设立的股份有限公司。2011年9月8日，设计院有限召开股东会议，全体股东一致同意设计院有限整体变更为股份有限公司，并以设计院有限截至2011年8月31日经审计的净资产197,845,055.22元为基数，按1：0.227451的比例折为股份公司4,500万股，每股面值为1元，由各发起人按其在设计院有限的原出资比例分别持有。2011年9月28日，股份公司在江苏省工商行政管理局完成工商变更登记，注册资本为4,500万元，注册号为：320594000030267。', '江苏省苏州工业园区八达街111号', '华东', '江苏', '苏州市', '1995-04-14', '27851.4728', '正常上市', '2014-12-31', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('603018', '华设集团', '交通领域建设工程勘察设计咨询业务。', '技术资料、图纸的复印，交通工程规划设计，工程管理服务，工程勘察设计、规划管理，工程和技术研究与试验发展，基础地质勘察，地质勘查技术服务，质检技术服务，测绘服务，建筑安装，建筑材料销售，信息系统集成及技术转让服务，软件开发，科技咨询及技术推广，物业管理，房屋租赁，自营和代理各类商品及技术的进出口业务，工程承包。（依法须经批准关部门批准后方可开展经营活动）许可项目：房屋建筑和市政基础设施项目工程总承包；各类工程建设活动；公路管理与养护（依法须经批准的项目，经相关部门批准后方可开展经营活动，具体经营项目以审批结果为准）一般项目：体育场地设施工程施工；园林绿化工程施工；砼结构构件制造；水泥制品制造；市政设施管理；环境保护专用设备制造；环保咨询服务；环境保护监测；水污染治理；固体废物治理；土壤污染治理与修复服务（依法须经批准的项目，凭营业执照依法自主开展经营活动）的项目，经相', '本公司是由江苏省交通规划设计院有限公司以截至2010年10月31日经审计的净资产人民币260,163,955元折成7,800万股股份整体变更设立的股份有限公司。2011年1月26日，江苏省交通规划设计院股份有限公司在江苏省工商行政管理局登记注册，取得《企业法人营业执照》（注册号：320000000055250)。', '江苏省南京市秦淮区紫云大道9号', '华东', '江苏', '南京市', '2005-08-12', '68378.0952', '正常上市', '2014-10-13', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('603357', '设计总院', '交通与城乡基础设施、资源与生态及环境、智能与信息化系统等工程提供专业技术服务。', '交通与城乡基础设施（道路、桥梁、隧道、港口、航道、轨道、交通工程、岩土、风景园林、给排水、建筑、结构等）、资源与生态及环境（保护、修复、防灾、治理与开发利用等）以及智能与信息化系统等工程的投资、规划、咨询、项目管理、勘察、设计、监理、检测、建造、运维、技术、装备和建筑材料开发与中介、总承包及对外承包工程。（依法须经批准的项目，经相关部门批准后方可开展经营活动）', '发行人由交规院有限整体变更设立。根据安徽省国资委《关于安徽省交通规划设计研究院有限公司改制上市有关事项的批复》，2014年2月27日，发行人召开创立大会，同意以交规院有限原股东作为发起人，以交规院有限截至2014年1月31日经审计的净资产308,733,885.33元，按1:0.6214的比例折为191,854,475股，将交规院有限整体变更为股份有限公司。2014年2月27日，华普天健会计师事务所（特殊普通合伙）出具《验资报告》（会验字[2014]0894号）对本次整体变更注册资本的实收情况进行了审验。2014年2月28日，公司在安徽省工商局完成工商变更登记并领取《企业法人营业执照》。', '安徽省合肥市高新区香樟大道180号', '华东', '安徽', '合肥市', '1994-03-11', '45454.2698', '正常上市', '2017-08-01', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('603458', '勘设股份', '工程咨询与工程承包业务。', '法律、法规、国务院决定规定禁止的不得经营；法律、法规、国务院决定规定应当许可（审批）的，经审批机关批准后凭许可（审批）文件经营；法律、法规、国务院决定规定无需许可（审批）的，市场主体自主选择经营。可承担国内外公路行业、市政行业（桥梁、道路、隧道）、建筑行业（建筑工程）、水运行业(航道、港口)的勘察设计、规划、技术咨询；工程勘察综合类业务；地质灾害防治工程评估、施工、监理、勘察、设计；工程咨询，节能评估、工程监理、工程施工、工程试验检测、工程测量、城市规划、科研、工程总承包等相关业务；对外派遣与其实力、规模、业绩适应的海外工程所需的劳务人员。养护运营信息化建设；工程相关材料、设备、软件的研发、生产、代理、技术转让、销售等业务。', '本公司是经贵州省人民政府“黔府函[2010]48号”文批复，于2010年4月30日由张林等405名自然人通过九家投资公司发起设立的股份有限公司。2010年4月30日，本公司在贵州省工商行政管理局注册登记，领取了《企业法人营业执照》（注册号：520000000007722），注册资本9,311.36万元。', '贵州省贵阳市贵阳国家高新技术产业开发区阳关大道附100号', '西南', '贵州', '贵阳市', '2010-04-30', '31380.4147', '正常上市', '2017-08-09', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
INSERT INTO `design_enterprise` VALUES ('603860', '中公高科', '公路养护决策咨询服务、路况快速检测设备生产与销售、公路养护信息系统开发与销售。', '技术开发、技术推广、技术转让、技术咨询、技术服务；销售计算机、软件及辅助设备、电子产品、仪器仪表、汽车；货物进出口、技术进出口、代理进出口；技术检测。（企业依法自主选择经营项目，开展经营活动；依法须经批准的项目，经相关部门批准后依批准的内容开展经营活动；不得从事本市产业政策禁止和限制类项目的经营活动。）', '本公司是依据《公司法》由中公高科有限整体变更设立的股份有限公司。本公司前身中公高科有限成立于2007年9月29日。2011年9月29日，交通运输部作出《关于中公高科（北京）养护科技有限公司股份制改造并在境内上市方案的批复》（交函政法[2011]第216号），同意中公高科有限股份制改造并在境内上市的方案；同意以中公高科有限现有股东中路高科及21名自然人作为发起人，采取整体变更方式，设立中公高科养护科技股份有限公司；同意以中公高科有限截至2011年8月31日经审计的账面净资产值按一定比例折为5,000万股。2011年11月16日，中公高科有限召开股东会并通过决议，同意中公高科有限整体变更为股份有限公司，公司名称为“中公高科养护科技股份有限公司”；同意以2011年8月31日作为整体变更的基准日，以截至2011年8月31日经德勤华永北京分所审计的中公高科有限净资产人民币7,054.91万元折为股份公司5,000万股股份（每股面值1元），其余净资产2,054.91万元计入股份公司资本公积；中公高科有限变更前原有股东作为股份公司发起人，以各自在中公高科有限中所占有的权益比例认购股份公司的股份；中公高科有限原债权债务由变更后的股份公司承继。2011年11月21日，公司召开创立大会暨第一次股东大会并通过决议，同意中公高科有限整体变更为股份有限公司。2011年11月21日，德勤华永北京分所出具德师京报（验）字（11）第0015号《验资报告》，确认本公司设立时的5,000万元注册资本已经缴足。2012年10月25日，立信所对德勤华永北京分所出具的德师京报（验）字（11）第0015号《验资报告》进行了复核，并出具了信会师报字[2012]第710039号《中公高科养护科技股份有限公司整体变更设立股份公司验资复核报告》。2011年11月28日，公司取得北京市工商行政管理局核发的整体变更为股份有限公司后的《企业法人营业执照》，工商注册号为110108010524258。', '北京市海淀区地锦路9号院4号楼-1至4层101', '华北', '北京', '北京市', '2007-09-29', '6668', '正常上市', '2017-08-02', '科学研究和技术服务业', '专业技术服务业', '建筑装饰', '工程咨询服务Ⅱ', '工程咨询服务Ⅲ');
